    public function getSourceContext()
    {
        return new Source("{% extends 'base2.html.twig' %}

{% block title %}Log in!{% endblock %}

{% block body %}

<div  style=\"display: flex; align-items: center;justify-content: center; margin-top: 50px;\">
<form method=\"post\">
    <div class=\"card\">
        <div class=\"card-body\">

    {% if error %}
        <div class=\"alert alert-danger\" style=\"display: flex; align-items: center;justify-content: center; margin-top: 50px;\">{{ error.messageKey|trans(error.messageData, 'security') }}</div>
    {% endif %}

    {% if app.user %}
        <div class=\"mb-3\">
            You are logged in as {{ app.user.userIdentifier }}, <a href=\"{{ path('app_logout') }}\">Logout</a>
        </div>
    {% endif %}

    <h1 class=\"h3 mb-3 font-weight-normal\">Please sign in</h1>
    <label for=\"inputEmail\" class=\"col-sm-10 col-form-label\">Email</label>
    <div class=\"col-sm-12\">
    <input type=\"email\" value=\"{{ last_username }}\" name=\"email\" id=\"inputEmail\" class=\"form-control\" autocomplete=\"email\" required autofocus></div>
    <label for=\"inputPassword\" class=\"col-sm-10 col-form-label\">Password</label>
    <div class=\"col-sm-12\">
    <input type=\"password\" name=\"password\" id=\"inputPassword\" class=\"form-control\" autocomplete=\"current-password\" required></div>

    <input type=\"hidden\" name=\"_csrf_token\"
           value=\"{{ csrf_token('authenticate') }}\"
    >
<br>
<div class=\"col-sm-10\" style=\" display: flex;align-items: center; justify-content: center; \"></div>
    <button class=\"btn btn-lg btn-primary\" type=\"submit\" >
        Sign in
    </button></div>
</div>


    <div class=\"\"col-12>
        <p class=\"small mb-0\">Don't have an Account?<a href=\"{{path('signup')}}\">Sign up</a></p>
    </div>
    <div class=\"\"col-12>
        <p class=\"small mb-0\"><a href=\"{{path('app_forgot_password_request')}}\">Forgot Password?</a></p>
    </div>
</div>
</form>

{% endblock %}
", "security/login.html.twig", "C:\\Users\\rayen\\Documents\\GitHub\\pi3\\TEAM_XL_projetPI_3A54\\templates\\security\\login.html.twig");
