{% block title %}Add Reply{% endblock %}

{% block body %}
    <div class=\"container\">
        <div class=\"row justify-content-center\">
            <div class=\"col-lg-8\">
                <div class=\"card shadow\">
                    <div class=\"card-header bg-primary text-white\">
                        <h2 class=\"text-center mb-0\">Add Reply</h2>
                    </div>
                    <div class=\"card-body\">
                        {{ form_start(form, {'attr': {'class': 'needs-validation', 'novalidate': 'novalidate'}}) }}
                        <div class=\"mb-3\">
                            {{ form_label(form.repdate, 'Reply Date', {'label_attr': {'class': 'form-label'}}) }}
                            {{ form_widget(form.repdate, {'attr': {'class': 'form-control', 'placeholder': 'Enter reply date'}}) }}
                            {{ form_errors(form.repdate) }}
                        </div>
                        <div class=\"mb-3\">
                            {{ form_label(form.content, 'Content', {'label_attr': {'class': 'form-label'}}) }}
                            {{ form_widget(form.content, {'attr': {'class': 'form-control', 'placeholder': 'Enter reply content'}}) }}
                            {{ form_errors(form.content) }}
                        </div>
                        <div class=\"mb-3\">
                            {{ form_label(form.reclamation, 'Reclamation', {'label_attr': {'class': 'form-label'}}) }}
                            {{ form_widget(form.reclamation, {'attr': {'class': 'form-control'}}) }}
                            {{ form_errors(form.reclamation) }}
                        </div>
                        <div class=\"text-center\">
                            <button type=\"submit\" class=\"btn btn-primary\">Submit</button>
                        </div>
                        {{ form_end(form) }}
                    </div>
                </div>
            </div>
        </div>
    </div>
{% endblock %}
